quick_router = matic.eth.contract(address=QUICKSWAP_ROUTER_ADDRESS, abi=QUICKSWAP_ROUTER_ABI)

stats = []
i = 0
for hash in hashes:
    try:
        i = i + 1
        res = matic.eth.getTransaction(hash[0])
        # print(res)
        gas_price = res['gasPrice']
        input_data = res['input']
        # print(input_data)
        readable = quick_router.decode_function_input(input_data)
        # print(readable)
        stats.append([hash[0], hash[1], readable[1]['path'], gas_price])
        print(stats)
        print(i)
    except Exception as e:
        print(e)

print(stats)
exit()

with open('stats_tx', 'wb') as fp:
    pickle.dump(stats, fp)
